add_action('admin_head', 'bibliapopup_tooltip_button');

function bibliapopup_tooltip_button() {
    global $typenow;
    if (!current_user_can('edit_posts') && !current_user_can('edit_pages')) {
        return;
    } if (!in_array($typenow, array('post', 'page')))
        return;
    if (get_user_option('rich_editing') == 'true') {
        add_filter('mce_external_plugins', 'bibliapopup_tooltip_add_tinymce_plugin');
        add_filter('mce_buttons', 'bibliapopup_tooltip_register_tinymce_plugin');
    }
}

function bibliapopup_tooltip_add_tinymce_plugin($plugin_array) {
    $plugin_array['ttip_shortcode_button'] = plugins_url('biblia-popup') . '/js/popup-plugin.js';
    return $plugin_array;
}

function bibliapopup_tooltip_register_tinymce_plugin($buttons) {
    array_push($buttons, "ttip_shortcode_button");
    return $buttons;
}

